let currDate = Date.getHours();
currDate = currDate.getHours();
let msg = '';

if(currDate >=1 && currDate <12){
    msg = 'Good Morning!';
}else if(currDate>=12 && currDate <18){
    msg = 'Good Afternoon!';
}else{
    msg= 'Good Night!';
}

function WelcomeMsg() {
    return (
     <h1>Hi Somnath, {msg}</h1>
    );
  }

